# Remove special characters, punctuation marks and spaces from the input string using regex
    # Use nltk.word_tokenize to split the input string into words
    # Use collections.Counter to count the frequency of each word
    # Use a list comprehension to extract the words that satisfy the regex condition

    special_chars = re.compile('[^a-zA-Z0-9]')
    punctuations = re.compile('[!"#$%&\'()*+,-./:;<=>?@[\\]^_`{|}~]+')

    def remove_special_characters(input_str):
        return special_chars.sub('', input_str)

    def remove_punctuation(input_str):
        return punctuations.sub(' ', input_str)

    def tokenize(input_str):
        return word_tokenize(remove_punctuation(remove_special_characters(input_str)))

    return Counter(tokenize(input_str))
